mapper.select:
select a.*, b.realname FROM house a LEFT JOIN users b on a.usersid = b.usersid where 1=1 

  and a.houseno = #{houseno}


  and a.usersid = #{usersid}


  and a.address = #{address}


  and a.mianji = #{mianji}


  and a.chaoxiang = #{chaoxiang}


  and a.status = #{status}


  and a.addtime = #{addtime}


  and a.memo = #{memo}
